/**
 * @param {*} data
 * @return {boolean}
 */
export function isAudioData(data: any): boolean;
/**
 * @param {object} data
 * @return {AudioData}
 */
export function toAudioData(data: object): any;
/**
 * @param {*} data
 * @return {boolean}
 */
export function isAudioBuffer(data: any): boolean;
/**
 * @param {object} data
 * @param {class}  AudioBuffer
 * @return {AudioBuffer}
 */
export function toAudioBuffer(data: object, AudioBuffer: any): any;
